INSERT INTO care_ops301_de VALUES ( '5-79', 'Reposition von Fraktur und Luxation', '', 'Exkl.:    Geschlossene Reposition ohne Osteosynthese (8-200 ff.)<br>Frakturosteosynthese an der Wirbelsäule (5-834)<br>Osteosynthese am knöchernen Thorax (5-346.5, 5-349.0)', 'Hinweis:  Die durchgeführten Einzelmaßnahmen zur operativen Versorgung einer Weichteilverletzung bei einer Fraktur oder Luxation sind gesondert zu kodieren:<br>Wunddebridement (5-893)<br>Gefäßnaht (5-388)<br>Nervennaht (5-044 ff.)<br>Kompartmentresektion (5-852.b ff.)<br>Muskel- oder Sehnennaht (5-853.1, 5-855.1, 5-855.2)<br>Muskel- oder Sehnennaht an der Hand (5-840.6 f.,  5-843.6)<br>Weichteildeckung (5-90)<br>Die Durchführung einer zweiten Osteosynthese, z.B. bei einer Zweietagen-Fraktur, ist gesondert zu kodieren.<br>Eine Mehrfragment-Fraktur wird als Fraktur mit mehr als zwei Fragmenten definiert. Dazu gehört auch eine Fraktur mit Biegungskeil. Eine Zweietagen-Fraktur besteht dagegen aus zwei Frakturen an unterschiedlichen Lokalisationen des gleichen Knochens.<br>Bei Fehlen der Angabe Einfach- oder Mehrfragment-Fraktur ist die Operation als Einfach-Fraktur zu kodieren<br>Die arthroskopisch assistierte Versorgung einer Fraktur ist gesondert zu kodieren (5-810.6)<br>Die Durchführung einer Osteotomie ist gesondert zu kodieren (5-781)<br>Eine Schraubenosteosynthese ist eine Osteosynthese, die nur mit Schrauben, ggf. mit zusätzlicher Unterlegscheibe, erfolgt<br>Eine Plattenosteosynthese ist eine Osteosynthese, die mit Platten und den dazugehörigen Schrauben, ggf. mit zusätzlichen Schrauben neben der Platte, erfolgt<br>Die Hämatomausräumung im Weichteilbereich bei einer Fraktur oder Luxation ist im Kode enthalten. Sie ist nur dann gesondert zu kodieren, wenn es sich um einen separaten Eingriff handelt', 'B', '2', '');
INSERT INTO care_ops301_de VALUES ( '5-790', 'Geschlossene Reposition einer Fraktur oder Epiphysenlösung mit Osteosynthese', 'Inkl.:    Versorgung kindlicher Frakturen', 'Exkl.:    Geschlossene Reposition einer Gelenkluxation mit Osteosynthese (5-79a)<br>Fixateur externe am Becken (5-798.6)<br>Fixateur externe am Hüftgelenk (5-799.8)', 'Hinweis:  Die arthroskopisch assistierte Versorgung einer Fraktur ist gesondert zu kodieren (5-810.6)<br>Hinweis:  Die Lokalisation ist in der 6. Stelle nach folgender Liste zu kodieren. Es sind jedoch nicht alle Verfahren an allen Lokalisationen durchführbar:<br>0   Skapula und Klavikula<br>1   Humerus proximal<br>2   Humerusschaft<br>3   Humerus distal<br>4   Radius proximal<br>5   Radiusschaft<br>6   Radius distal<br>7   Ulna proximal<br>8   Ulnaschaft<br>9   Ulna distal<br>a   Karpale<br>b   Metakarpale<br>c   Phalangen Hand<br>d   Becken<br>e   Schenkelhals<br>f   Femur proximal<br>g   Femurschaft<br>h   Femur distal<br>j   Patella<br>k   Tibia proximal<br>m   Tibiaschaft<br>n   Tibia distal<br>p   Fibula proximal<br>q   Fibulaschaft<br>r   Fibula distal<br>s   Talus<br>t   Kalkaneus<br>u   Tarsale<br>v   Metatarsale<br>w   Phalangen Fuß<br>x   Sonstige<br>y   N.n.bez.', 'B', '3', '');
