INSERT INTO care_icd10_de VALUES ( '(M00-M99)', 'Krankheiten des Muskel-Skelett-Systems und des Bindegewebes', '-0T', 'p', '', 'Exkl.: Angeborene Fehlbildungen, Deformitäten und Chromosomenanomalien<br>(Q00-Q99)<br>Bestimmte infektiöse und parasitäre Krankheiten (A00-B99)<br>Bestimmte Zustände, die ihren Ursprung in der Perinatalperiode<br>haben (P00-P96)<br>Endokrine, Ernährungs- und Stoffwechselkrankheiten (E00-E90)<br>Kompartmentsyndrom (T79.6)<br>Komplikationen der Schwangerschaft, der Geburt und des<br>Wochenbettes (O00-O99)<br>Neubildungen (C00-D48)<br>Symptome und abnorme klinische und Laborbefunde, die anderenorts<br>nicht klassifiziert sind (R00-R99)<br>Verletzungen, Vergiftungen und bestimmte andere Folgen äußerer<br>Ursachen (S00-T98)', '', '-', '0', '', '', '');
