INSERT INTO care_icd10_de VALUES ( 'M95-M99', 'Sonstige Krankheiten des Muskel-Skelett-Systems und des<br>Bindegewebes', '-0G', 'table', '', '', '', '-', '0', '', 'Die folgende Subklassifikation zur Angabe des Beteiligungsortes kann<br>wahlweise mit den passenden Schlüsselnummern des Kapitels XIII benutzt<br>werden. Da örtliche Erweiterungen oder fachspezifische Adaptationen der<br>Klassifikation sich in der Stellenzahl der Schlüsselnummern unterscheiden<br>können, wird vorgeschlagen, diese ergänzende Ortsangabe besonders zu<br>kennzeichnen (z.B. durch ein zusätzliches Signierkästchen). Hiervon<br>abweichende Subklassifikationen für Kniegelenkschäden, Rückenleiden und<br>anderenorts nicht klassifizierte biomechanische Funktionsstörungen finden<br>sich unter M23, der Krankheitsgruppe M40-M54 und unter M99. Werden diese<br>Subklassifikationen als fünfte Stellen bei Kategorien benutzt, die nicht<br>vierstellig unterteilt sind, so sollte die vierte Stelle mit dem<br>Kleinbuchstaben x besetzt werden.', '');
INSERT INTO care_icd10_de VALUES ( '(M00-M25)', 'Arthropathien', '-1T', 'p', ' Krankheiten, die vorwiegend an den peripheren (Extremitäten-) Gelenken<br>auftreten', '', '', '-', '1', '', '', '');
INSERT INTO care_icd10_de VALUES ( '(M00-M03)', 'Infektiöse Arthropathien', '-2T', 'p', '', '', ' Hinweis: Diese Gruppe enthält Gelenkkrankheiten durch Mikroorganismen.<br>Aufgrund der ätiologischen Zusammenhänge wird zwischen folgenden<br>Typen unterschieden:<br>P           a)  direkte Gelenkinfektion: Die Erreger wandern in das<br>Synovialgewebe ein, ihre Antigene sind im Gelenk nachweisbar.<br>P           b)  indirekte Gelenkinfektion: Es wird wiederum zwischen zwei<br>Typen unterschieden:<br>P               -   reaktive Arthritis: Es ist zwar eine Infektion des<br>Gesamtorganismus erwiesen, aber im Gelenk können weder<br>Erreger noch deren Antigene nachgewiesen werden.<br>P               -   postinfektiöse Arthritis: Es läßt sich zwar ein<br>Erregerantigen nachweisen, aber der Erreger selbst ist nur<br>inkonstant und seine lokale Vermehrung nicht nachweisbar.', '-', '2', '', '', '');
