INSERT INTO care_ops301_de VALUES ( '9-402.1', ': Integrierte klinisch-psychosomatische Komplexbehandlung', '', '', 'Hinweis:  Stationäre somatische und psychosomatische Behandlung  bei akuten und chronischen somatischen Erkrankungen  mit psychischer Komorbidität und Copingstörungen,  neben der somatischen Therapie durch  ärztliche/psychologische Einzeltherapie (100  Min./Wo.) und Einsatz spezifischer  psychotherapeutischer Techniken (360 Min./Wo.) im  standardisierten Setting nach den Regeln der  psychosomatischen und psychotherapeutischen Medizin  oder der Pädiatrie', 'B', '4', '');
INSERT INTO care_ops301_de VALUES ( '9-402.2', ': Psychosomatische und psychotherapeutische Krisenintervention als Komplexbehandlung', '', '', 'Hinweis:  Stationäre Kurztherapie mit umgrenztem Therapieziel zur  Stabilisierung bei akuter Dekompensation  (Verschiebung der Therapie-Dosis zu höherem Anteil an  Einzelpsychotherapie im Vergleich zu 9-402.0) nach  den Regeln der psychosomatischen und  psychotherapeutischen Medizin', 'B', '4', '');
INSERT INTO care_ops301_de VALUES ( '9-403', 'Sozial- und neuropädiatrische Therapie', '', '', 'Hinweis:  Operationalisierte individuelle Therapie durch multidisziplinäre Teams bei drohender oder manifester Behinderung, Entwicklungs- und Verhaltensstörung sowie seelischen Störungen, Anleitung von Bezugspersonen, Einsatz u. a. von Psychotherapie, Familiengesprächen, Entspannungsverfahren, sozialpädiatrischer, heilpädagogischer, psychosomatischer Therapie (täglich mindestens 3 Maßnahmen)', 'B', '3', '');
INSERT INTO care_ops301_de VALUES ( '9-404', 'Neuropsychologische Therapie', '', '', 'Hinweis:  Therapie beeinträchtigter kognitiver, affektiver und verhaltensbezogener Funktionen (Orientierung, Aufmerksamkeit, Wahrnehmung, Lernen und Gedächtnis, Planen und Problemlösen, Affekt- und Verhaltenskontrolle, soziale Kompetenz) bei Patienten mit angeborenen oder erworbenen Hirnschädigungen basierend auf kognitions-psychologischen, lerntheoretischen und funktional-neuroanatomischen Erkenntnissen', 'B', '3', '');
