INSERT INTO care_icd10_de VALUES ( '(E00-E90)', 'Endokrine, Ernährungs- und Stoffwechselkrankheiten', '-0T', 'p', '', 'Exkl.: Komplikationen der Schwangerschaft, der Geburt und des<br>Wochenbettes (O00-O99)<br>Symptome und abnorme klinische und Laborbefunde, die anderenorts<br>nicht klassifiziert sind (R00-R99)<br>Transitorische endokrine und Stoffwechselstörungen, die für den<br>Feten und das Neugeborene spezifisch sind (P70-P74)', 'Hinweis: Alle Neubildungen, ob funktionell aktiv oder nicht, sind in<br>Kapitel II klassifiziert. Zutreffende Schlüsselnummern dieses<br>Krankheitskapitels (d.h. E05.8, E07.0, E16-E31, E34.-) können<br>zusätzlich benutzt werden zur Angabe der funktionellen Aktivität<br>einer Neubildung, eines ektopen endokrinen Gewebes sowie der über-<br>oder Unterfunktion endokriner Drüsen durch Neubildungen oder<br>sonstige anderenorts klassifizierte Zustände.', '-', '0', '', '', '');
