INSERT INTO care_icd10_de VALUES ( '(E40-E46)', 'Mangelernährung', '-1T', 'p', '', 'Exkl.: Alimentäre Anämien (D50-D53)<br>Folgen der Energie- und Eiweißmangelernährung (E64.0)<br>Hungertod (T73.0)<br>Intestinale Malabsorption (K90.-)<br>Kachexie infolge HIV-Krankheit [Slim disease] (B22.2)', ' Hinweis: Der Grad der Unterernährung wird gewöhnlich mittels des Gewichtes<br>ermittelt und in Standardabweichungen vom Mittelwert der<br>entsprechenden Bezugspopulation dargestellt. Liegen eine oder<br>mehrere vorausgegangene Messungen vor, so ist eine fehlende<br>Gewichtszunahme bei Kindern bzw. eine Gewichtsabnahme bei Kindern<br>oder Erwachsenen in der Regel ein Anzeichen für eine<br>Mangelernährung.<br>P           Liegt nur eine Messung vor, so stützt sich die Diagnose auf<br>Annahmen und ist ohne weitere klinische Befunde oder<br>Laborergebnisse nicht endgültig. In jenen außergewöhnlichen<br>Fällen, bei denen kein Gewichtswert vorliegt, sollte man sich auf<br>klinische Befunde verlassen. Bei Gewichtswerten unterhalb des<br>Mittelwertes der Bezugspopulation besteht mit hoher<br>Wahrscheinlichkeit dann eine erhebliche Unterernährung, wenn der<br>Meßwert 3 oder mehr Standardabweichungen unter dem Mittelwert der<br>Bezugspopulation liegt
INSERT INTO care_icd10_de VALUES ( 'E40', 'Kwashiorkor', 'M3T', 'c', '', 'Exkl.: Kwashiorkor-Marasmus (E42)', '', 'M', '3', 'Erhebliche Mangelernährung mit alimentärem Ödem und Pigmentstörung<br>der Haut und der Haare.', '', '');
INSERT INTO care_icd10_de VALUES ( 'E41', 'Alimentärer Marasmus', 'M3T', 'c', 'Erhebliche Mangelernährung mit Marasmus', 'Exkl.: Kwashiorkor-Marasmus (E42)', '', 'M', '3', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'E42', 'Kwashiorkor-Marasmus', 'M3T', 'c', 'Erhebliche Energie- und Eiweißmangelernährung [wie unter E43<br>aufgeführt]:<br>. intermediäre Form<br>. mit Anzeichen von Kwashiorkor und Marasmus gleichzeitig', '', '', 'M', '3', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'E43', 'Nicht näher bezeichnete erhebliche Energie- und<br>Eiweißmangelernährung', 'M3T', 'c', 'Hungerödem', '', '', 'M', '3', 'Erheblicher Gewichtsverlust [Unterernährung] [Kachexie] bei<br>Kindern oder Erwachsenen oder fehlende Gewichtszunahme bei<br>Kindern, die zu einem Gewichtswert führen, der mindestens 3<br>Standardabweichungen unter dem Mittelwert der Bezugspopulation<br>liegt (oder eine ähnliche Abweichung in anderen statistischen<br>Verteilungen). Wenn nur eine Gewichtsmessung vorliegt, besteht mit<br>hoher Wahrscheinlichkeit eine erhebliche Unterernährung, wenn der<br>Gewichtswert 3 oder mehr Standardabweichungen unter dem Mittelwert<br>der Bezugspopulation liegt.', '', '');
