INSERT INTO care_icd10_de VALUES ( 'E44.0', 'Mäßige Energie- und Eiweißmangelernährung', 'N4T', 'c', '', '', '', 'N', '4', 'Gewichtsverlust bei Kindern oder Erwachsenen oder fehlende<br>Gewichtszunahme bei Kindern, die zu einem Gewichtswert führen, der<br>2 oder mehr, aber weniger als 3 Standardabweichungen unter dem<br>Mittelwert der Bezugspopulation liegt (oder einer ähnlichen<br>Abweichung in anderen statistischen Verteilungen). Wenn nur eine<br>Gewichtsmessung vorliegt, besteht mit hoher Wahrscheinlichkeit<br>eine mäßige Energie- und Eiweißmangelernährung, wenn der<br>Gewichtswert 2 oder mehr, aber weniger als 3 Standardabweichungen<br>unter dem Mittelwert der Bezugspopulation liegt.', '', '');
INSERT INTO care_icd10_de VALUES ( 'E44.1', 'Leichte Energie- und Eiweißmangelernährung', 'N4T', 'c', '', '', '', 'N', '4', 'Gewichtsverlust bei Kindern oder Erwachsenen oder fehlende<br>Gewichtszunahme bei Kindern, die zu einem Gewichtswert führen, der<br>1 oder mehr, aber weniger als 2 Standardabweichungen unter dem<br>Mittelwert der Bezugspopulation liegt (oder einer ähnlichen<br>Abweichung in anderen statistischen Verteilungen). Wenn nur eine<br>Gewichtsmessung vorliegt, besteht mit hoher Wahrscheinlichkeit<br>eine leichte Energie- und Eiweißmangelernährung, wenn der<br>Gewichtswert 1 oder mehr, aber weniger als 2 Standardabweichungen<br>unter dem Mittelwert der Bezugspopulation liegt.', '', '');
