INSERT INTO care_icd10_de VALUES ( '(F00-F09)', 'Organische, einschließlich symptomatischer psychischer Störungen', '-1T', 'p', '', '', '', '-', '1', 'Dieser Abschnitt umfaßt eine Reihe psychischer Krankheiten mit<br>nachweisbarer Ätiologie in einer zerebralen Krankheit, einer Hirnverletzung<br>oder einer anderen Schädigung, die zu einer Hirnfunktionsstörung führt. Die<br>Funktionsstörung kann primär sein, wie bei Krankheiten, Verletzungen oder<br>Störungen, die das Gehirn direkt oder in besonderem Maße betreffen
INSERT INTO care_icd10_de VALUES ( 'F00.-*', 'Demenz bei Alzheimer-Krankheit (G30.-+)', 'O3T', 'c', '', '', '', 'O', '3', 'Die Alzheimer-Krankheit ist eine primär degenerative zerebrale<br>Krankheit mit unbekannter Ätiologie und charakteristischen<br>neuropathologischen und neurochemischen Merkmalen. Sie beginnt<br>meist schleichend und entwickelt sich langsam aber stetig über<br>einen Zeitraum von mehreren Jahren.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F00.0*', 'Demenz bei Alzheimer-Krankheit, mit frühem Beginn (Typ 2)<br>(G30.0+)', 'O4T', 'c', 'Alzheimer-Krankheit, Typ 2<br>Präsenile Demenz vom Alzheimer-Typ<br>Primär degenerative Demenz vom Alzheimer-Typ, präseniler Beginn', '', '', 'O', '4', 'Demenz bei Alzheimer-Krankheit mit Beginn vor dem 65. Lebensjahr.<br>Der Verlauf weist eine vergleichsweise rasche Verschlechterung<br>auf, es bestehen deutliche und vielfältige Störungen der höheren<br>kortikalen Funktionen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F00.1*', 'Demenz bei Alzheimer-Krankheit, mit spätem Beginn (Typ 1)<br>(G30.1+)', 'O4T', 'c', 'Alzheimer-Krankheit, Typ 1<br>Primär degenerative Demenz vom Alzheimer-Typ, seniler Beginn<br>Senile Demenz vom Alzheimer-Typ (SDAT)', '', '', 'O', '4', 'Demenz bei Alzheimer-Krankheit mit Beginn nach dem 65. Lebensjahr,<br>meist in den späten 70er Jahren oder danach, mit langsamer<br>Progredienz und mit Gedächtnisstörungen als Hauptmerkmal.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F00.2*', 'Demenz bei Alzheimer-Krankheit, atypische oder gemischte Form<br>(G30.8+)', 'O4T', 'c', 'Atypische Demenz vom Alzheimer-Typ', '', '', 'O', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F00.9*', 'Demenz bei Alzheimer-Krankheit, nicht näher bezeichnet (G30.9+)', 'O4T', 'c', '', '', '', 'O', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.-', 'Vaskuläre Demenz', 'M3T', 'c', 'Inkl.:   Arteriosklerotische Demenz', '', '', 'M', '3', 'Die vaskuläre Demenz ist das Ergebnis einer Infarzierung des<br>Gehirns als Folge einer vaskulären Krankheit, einschließlich der<br>zerebrovaskulären Hypertonie. Die Infarkte sind meist klein,<br>kumulieren aber in ihrer Wirkung. Der Beginn liegt gewöhnlich im<br>späteren Lebensalter.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.0', 'Vaskuläre Demenz mit akutem Beginn', 'N4T', 'c', '', '', '', 'N', '4', 'Diese entwickelt sich meist sehr schnell nach einer Reihe von<br>Schlaganfällen als Folge von zerebrovaskulärer Thrombose, Embolie<br>oder Blutung. In seltenen Fällen kann eine einzige massive<br>Infarzierung die Ursache sein.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.1', 'Multiinfarkt-Demenz', 'M4T', 'c', 'Vorwiegend kortikale Demenz', '', '', 'M', '4', 'Sie beginnt allmählich, nach mehreren vorübergehenden ischämischen<br>Episoden (TIA), die eine Anhäufung von Infarkten im Hirngewebe<br>verursachen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.2', 'Subkortikale vaskuläre Demenz', 'N4T', 'c', '', '', '', 'N', '4', 'Hierzu zählen Fälle mit Hypertonie in der Anamnese und<br>ischämischen Herden im Marklager der Hemisphären. Im Gegensatz zur<br>Demenz bei Alzheimer-Krankheit, an die das klinische Bild<br>erinnert, ist die Hirnrinde gewöhnlich intakt.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.3', 'Gemischte kortikale und subkortikale vaskuläre Demenz', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.8', 'Sonstige vaskuläre Demenz', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F01.9', 'Vaskuläre Demenz, nicht näher bezeichnet', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.-*', 'Demenz bei anderenorts klassifizierten Krankheiten', 'O3T', 'c', '', '', '', 'O', '3', 'Formen der Demenz, bei denen eine andere Ursache als die<br>Alzheimer-Krankheit oder eine zerebrovaskuläre Krankheit vorliegt<br>oder vermutet wird. Sie kann in jedem Lebensalter auftreten,<br>selten jedoch im höheren Alter.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.0*', 'Demenz bei Pick-Krankheit (G31.0+)', 'O4T', 'c', '', '', '', 'O', '4', 'Eine progrediente Demenz mit Beginn im mittleren Lebensalter,<br>charakterisiert durch frühe, langsam fortschreitende<br>Persönlichkeitsänderung und Verlust sozialer Fähigkeiten. Die<br>Krankheit ist gefolgt von Beeinträchtigungen von Intellekt,<br>Gedächtnis und Sprachfunktionen mit Apathie, Euphorie und<br>gelegentlich auch extrapyramidalen Phänomenen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.1*', 'Demenz bei Creutzfeldt-Jakob-Krankheit (A81.0+)', 'O4T', 'c', '', '', '', 'O', '4', 'Eine progrediente Demenz mit vielfältigen neurologischen Symptomen<br>als Folge spezifischer neuropathologischer Veränderungen, die<br>vermutlich durch ein übertragbares Agens verursacht werden. Beginn<br>gewöhnlich im mittleren oder höheren Lebensalter, Auftreten jedoch<br>in jedem Erwachsenenalter möglich. Der Verlauf ist subakut und<br>führt innerhalb von ein bis zwei Jahren zum Tode.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.2*', 'Demenz bei Chorea Huntington (G10+)', 'O4T', 'c', 'Demenz bei Huntington-Krankheit', '', '', 'O', '4', 'Eine Demenz, die im Rahmen einer ausgeprägten Hirndegeneration<br>auftritt. Die Störung ist autosomal dominant erblich. Die<br>Symptomatik beginnt typischerweise im dritten und vierten<br>Lebensjahrzehnt. Bei langsamer Progredienz führt die Krankheit<br>meist innerhalb von 10 - 15 Jahren zum Tode.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.3*', 'Demenz bei primärem Parkinson-Syndrom (G20+)', 'O4T', 'c', 'Demenz bei:<br>. Paralysis agitans<br>. Parkinsonismus oder Parkinson-Krankheit', '', '', 'O', '4', 'Eine Demenz, die sich im Verlauf einer Parkinson-Krankheit<br>entwickelt. Bisher konnten allerdings noch keine<br>charakteristischen klinischen Merkmale beschrieben werden.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.4*', 'Demenz bei HIV-Krankheit [Humane Immundefizienz-Viruskrankheit]<br>(B22.0+)', 'O4T', 'c', '', '', '', 'O', '4', 'Eine Demenz, die sich im Verlauf einer HIV-Krankheit entwickelt,<br>ohne gleichzeitige andere Krankheit oder Störung, die das<br>klinische Bild erklären könnte.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F02.8*', 'Demenz bei anderenorts klassifizierten Krankheitsbildern', 'O4T', 'c', 'Demenz bei:<br>. Epilepsie (G40.-+)<br>. hepatolentikulärer Degeneration [M. Wilson] (E83.0+)<br>. Hyperkalziämie (E83.5+)<br>. Hypothyreose, erworben (E01+, E03.-+)<br>. Intoxikationen (T36-T65+)<br>. Multipler Sklerose (G35+)<br>. Neurosyphilis (A52.1+)<br>. Niazin-Mangel [Pellagra] (E52+)<br>. Panarteriitis nodosa (M30.0+)<br>. systemischem Lupus erythematodes (M32.-+)<br>. Trypanosomiasis (B56.-+, B57.-+)<br>. Vitamin-B12-Mangel (E53.8+)<br>. zerebraler Lipidstoffwechselstörung (E75.-+)', '', '', 'O', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F03', 'Nicht näher bezeichnete Demenz', 'M3T', 'c', 'Präsenil:<br>. Demenz o.n.A.<br>. Psychose o.n.A.<br>Primäre degenerative Demenz o.n.A.<br>Senil:<br>. Demenz:<br>  . depressiver oder paranoider Typus<br>  . o.n.A.<br>. Psychose o.n.A.', 'Exkl.: Senile Demenz mit Delir oder akutem Verwirrtheitszustand<br>(F05.1)<br>Senilität o.n.A. (R54)', '', 'M', '3', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F04', 'Organisches amnestisches Syndrom, nicht durch Alkohol oder andere<br>psychotrope Substanzen bedingt', 'M3T', 'c', 'Korsakow-Psychose oder -Syndrom, nicht alkoholbedingt', 'Exkl.: Amnesie:<br>. anterograd (R41.1)<br>. dissoziativ (F44.0)<br>. retrograd (R41.2)<br>. o.n.A. (R41.3)<br>Korsakow-Syndrom:<br>. alkoholbedingt oder nicht näher bezeichnet (F10.6)<br>. durch andere psychotrope Substanzen bedingt (F11-F19,<br>vierte Stelle .6)', '', 'M', '3', 'Ein Syndrom mit deutlichen Beeinträchtigungen des Kurz- und<br>Langzeitgedächtnisses, bei erhaltenem Immediatgedächtnis. Es<br>finden sich eine eingeschränkte Fähigkeit, neues Material zu<br>erlernen und zeitliche Desorientierung. Konfabulation kann ein<br>deutliches Merkmal sein, aber Wahrnehmung und andere kognitive<br>Funktionen, einschließlich Intelligenz, sind gewöhnlich intakt.<br>Die Prognose ist abhängig vom Verlauf der zugrundeliegenden<br>Läsion.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F05.-', 'Delir, nicht durch Alkohol oder andere psychotrope Substanzen<br>bedingt', 'M3T', 'c', 'Inkl.:   Akut oder subakut:<br>. exogener Reaktionstyp<br>. hirnorganisches Syndrom<br>. psychoorganisches Syndrom<br>. Psychose bei Infektionskrankheit<br>. Verwirrtheitszustand (nicht alkoholbedingt)', 'Exkl.: Delirium tremens, alkoholbedingt oder nicht näher<br>bezeichnet (F10.4)', '', 'M', '3', 'Ein ätiologisch unspezifisches hirnorganisches Syndrom, das<br>charakterisiert ist durch gleichzeitig bestehende Störungen des<br>Bewußtseins und der Aufmerksamkeit, der Wahrnehmung, des Denkens,<br>des Gedächtnisses, der Psychomotorik, der Emotionalität und des<br>Schlaf-Wach-Rhythmus. Die Dauer ist sehr unterschiedlich und der<br>Schweregrad reicht von leicht bis zu sehr schwer.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F05.0', 'Delir ohne Demenz', 'M4T', 'c', '', '', '', 'M', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F05.1', 'Delir bei Demenz', 'M4T', 'c', '', '', '', 'M', '4', 'Diese Kodierung soll für Krankheitsbilder verwendet werden, die<br>die oben erwähnten Kriterien erfüllen, sich aber im Verlauf einer<br>Demenz entwickeln (F00-F03).', '', '');
INSERT INTO care_icd10_de VALUES ( 'F05.8', 'Sonstige Formen des Delirs', 'N4T', 'c', 'Delir mit gemischter Ätiologie', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F05.9', 'Delir, nicht näher bezeichnet', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.-', 'Andere psychische Störungen aufgrund einer Schädigung oder<br>Funktionsstörung des Gehirns oder einer körperlichen Krankheit', 'M3T', 'c', '', 'Exkl.: In Verbindung mit Demenz, wie unter F00-F03 beschrieben<br>Psychische Störung mit Delir (F05.-)<br>Störungen durch Alkohol oder andere psychotrope<br>Substanzen (F10-F19)', '', 'M', '3', 'Diese Kategorie umfaßt verschiedene Krankheitsbilder, die<br>ursächlich mit einer Hirnfunktionsstörung in Zusammenhang stehen<br>als Folge von primär zerebralen Krankheiten, systemischen<br>Krankheiten, die sekundär das Gehirn betreffen, exogenen toxischen<br>Substanzen oder Hormonen, endokrinen Störungen oder anderen<br>körperlichen Krankheiten.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.0', 'Organische Halluzinose', 'N4T', 'c', 'Organisch bedingtes halluzinatorisches Zustandsbild (nicht<br>alkoholbedingt)', 'Exkl.: Alkoholhalluzinose (F10.5)<br>Schizophrenie (F20.-)', '', 'N', '4', 'Eine Störung mit ständigen oder immer wieder auftretenden, meist<br>optischen oder akustischen Halluzinationen bei klarer<br>Bewußtseinslage. Sie können vom Patienten als Halluzinationen<br>erkannt werden. Die Halluzinationen können wahnhaft verarbeitet<br>werden, Wahn dominiert aber nicht das klinische Bild. Die<br>Krankheitseinsicht kann erhalten bleiben.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.1', 'Organische katatone Störung', 'N4T', 'c', '', 'Exkl.: Katatone Schizophrenie (F20.2)<br>Stupor:<br>. dissoziativ (F44.2)<br>. o.n.A. (R40.1)', '', 'N', '4', 'Eine Störung mit verminderter (Stupor) oder gesteigerter<br>(Erregung) psychomotorischer Aktivität in Verbindung mit katatonen<br>Symptomen. Das Erscheinungsbild kann zwischen den beiden Extremen<br>der psychomotorischen Störung wechseln.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.2', 'Organische wahnhafte [schizophreniforme] Störung', 'N4T', 'c', 'Paranoide und paranoid-halluzinatorische organisch bedingte<br>Zustandsbilder<br>Schizophreniforme Psychose bei Epilepsie', 'Exkl.: Akute vorübergehende psychotische Störungen (F23.-)<br>Anhaltende wahnhafte Störungen (F22.-)<br>Durch psychotrope Substanzen induzierte psychotische<br>Störungen (F11-F19, vierte Stelle .5)<br>Schizophrenie (F20.-)', '', 'N', '4', 'Eine Störung, bei der anhaltende oder immer wieder auftretende<br>Wahnideen das klinische Bild bestimmen. Die Wahnideen können von<br>Halluzinationen begleitet werden. Einige Merkmale, die auf<br>Schizophrenie hinweisen, wie bizarre Halluzinationen oder<br>Denkstörungen, können vorliegen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.3', 'Organische affektive Störungen', 'N4T', 'c', '', 'Exkl.: Nichtorganische oder nicht näher bezeichnete affektive<br>Störungen (F30-F39)', '', 'N', '4', 'Störungen, die durch eine Veränderung der Stimmung oder des<br>Affektes charakterisiert sind, meist zusammen mit einer<br>Veränderung der gesamten Aktivitätslage. Depressive, hypomanische,<br>manische oder bipolare Zustandsbilder (F30-F38) sind möglich,<br>entstehen jedoch als Folge einer organischen Störung.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.4', 'Organische Angststörung', 'N4T', 'c', '', 'Exkl.: Nichtorganisch bedingte oder nicht näher bezeichnete<br>Angststörungen (F41.-)', '', 'N', '4', 'Eine Störung, charakterisiert durch die wesentlichen deskriptiven<br>Merkmale einer generalisierten Angststörung (F41.1), einer<br>Panikstörung (F41.0) oder einer Kombination von beiden, jedoch als<br>Folge einer organischen Störung.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.5', 'Organische dissoziative Störung', 'N4T', 'c', '', 'Exkl.: Nichtorganisch bedingte oder nicht näher bezeichnete<br>dissoziative Störungen [Konversionsstörungen] (F44.-)', '', 'N', '4', 'Eine Störung, charakterisiert durch den teilweisen oder völligen<br>Verlust der normalen Integration von Erinnerungen an die<br>Vergangenheit, des Identitätsbewußtseins und der unmittelbaren<br>Wahrnehmungen sowie der Kontrolle von Körperbewegungen (F44.-),<br>jedoch als Folge einer organischen Störung.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.6', 'Organische emotional labile [asthenische] Störung', 'N4T', 'c', '', 'Exkl.: Nichtorganisch bedingte oder nicht näher bezeichnete<br>somatoforme Störungen (F45.-)', '', 'N', '4', 'Eine Störung, charakterisiert durch Affektdurchlässigkeit oder<br>-labilität, Ermüdbarkeit sowie eine Vielzahl körperlicher<br>Mißempfindungen (z.B. Schwindel) und Schmerzen, jedoch als Folge<br>einer organischen Störung.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.7', 'Leichte kognitive Störung', 'N4T', 'c', '', '', '', 'N', '4', 'Eine Störung, die charakterisiert ist durch Gedächtnisstörungen,<br>Lernschwierigkeiten und die verminderte Fähigkeit, sich längere<br>Zeit auf eine Aufgabe zu konzentrieren. Oft besteht ein Gefühl<br>geistiger Ermüdung bei dem Versuch, Aufgaben zu lösen. Objektiv<br>erfolgreiches Lernen wird subjektiv als schwierig empfunden.<br>Keines dieser Symptome ist so schwerwiegend, daß die Diagnose<br>einer Demenz (F00-F03) oder eines Delirs (F05.-) gestellt werden<br>kann. Die Diagnose sollte nur in Verbindung mit einer körperlichen<br>Krankheit gestellt und bei Vorliegen einer anderen psychischen<br>oder Verhaltensstörung aus dem Abschnitt F10-F99 nicht verwandt<br>werden. Diese Störung kann vor, während oder nach einer Vielzahl<br>von zerebralen oder systemischen Infektionen oder anderen<br>körperlichen Krankheiten auftreten. Der direkte Nachweis einer<br>zerebralen Beteiligung ist aber nicht notwendig. Die Störung wird<br>vom postenzephalitischen (F07.1) und vom postkontusionellen<br>Syndrom (F07.2) durch ihre andere Ätiologie, die wenig variablen,<br>insgesamt leichteren Symptome und die zumeist kürzere Dauer<br>unterschieden.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.8', 'Sonstige näher bezeichnete organische psychische Störungen<br>aufgrund einer Schädigung oder Funktionsstörung des Gehirns oder<br>einer körperlichen Krankheit', 'N4T', 'c', 'Epileptische Psychose o.n.A.', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F06.9', 'Nicht näher bezeichnete organische psychische Störung aufgrund<br>einer Schädigung oder Funktionsstörung des Gehirns oder einer<br>körperlichen Krankheit', 'N4T', 'c', 'Hirnorganisches Syndrom o.n.A.<br>Organische psychische Störung o.n.A.', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F07.-', 'Persönlichkeits- und Verhaltensstörung aufgrund einer Krankheit,<br>Schädigung oder Funktionsstörung des Gehirns', 'M3T', 'c', '', '', '', 'M', '3', 'Eine Veränderung der Persönlichkeit oder des Verhaltens kann Rest-<br>oder Begleiterscheinung einer Krankheit, Schädigung oder<br>Funktionsstörung des Gehirns sein.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F07.0', 'Organische Persönlichkeitsstörung', 'N4T', 'c', 'Frontalhirnsyndrom<br>Leukotomiesyndrom<br>Lobotomiesyndrom<br>Organisch:<br>. Pseudopsychopathie<br>. pseudoretardierte Persönlichkeit<br>Persönlichkeitsstörung bei limbischer Epilepsie', 'Exkl.: Andauernde Persönlichkeitsänderung nach:<br>. Extrembelastung (F62.0)<br>. psychiatrischer Krankheit (F62.1)<br>Organisches Psychosyndrom nach Schädelhirntrauma<br>(F07.2)<br>Persönlichkeitsstörungen (F60-F61)<br>Postenzephalitisches Syndrom (F07.1)', '', 'N', '4', 'Diese Störung ist charakterisiert durch eine auffällige<br>Veränderung des gewohnten prämorbiden Verhaltensmusters und<br>betrifft die Äußerung von Affekten, Bedürfnissen und Impulsen.<br>Eine Beeinträchtigung der kognitiven Fähigkeiten, des<br>Denkvermögens und ein verändertes Sexualverhalten können ebenfalls<br>Teil des klinischen Bildes sein.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F07.1', 'Postenzephalitisches Syndrom', 'N4T', 'c', '', 'Exkl.: Organische Persönlichkeitsstörung (F07.0)', '', 'N', '4', 'Anhaltende unspezifische und uneinheitliche Verhaltensänderung<br>nach einer viralen oder bakteriellen Enzephalitis. Das Syndrom ist<br>reversibel
INSERT INTO care_icd10_de VALUES ( 'F07.2', 'Organisches Psychosyndrom nach Schädelhirntrauma', 'M4T', 'c', 'Postkontusionelles Syndrom (Enzephalopathie)<br>Posttraumatisches (organisches) Psychosyndrom, nicht psychotisch', '', '', 'M', '4', 'Das Syndrom folgt einem Schädeltrauma, das meist schwer genug ist,<br>um zur Bewußtlosigkeit zu führen. Es besteht aus einer Reihe<br>verschiedenartiger Symptome, wie Kopfschmerzen, Schwindel,<br>Erschöpfung, Reizbarkeit, Schwierigkeiten bei Konzentration und<br>geistigen Leistungen, Gedächtnisstörungen, Schlafstörungen und<br>verminderter Belastungsfähigkeit für Streß, emotionale Reize oder<br>Alkohol.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F07.8', 'Sonstige organische Persönlichkeits- und Verhaltensstörungen<br>aufgrund einer Krankheit, Schädigung oder Funktionsstörung des<br>Gehirns', 'N4T', 'c', 'Rechts-hemisphärische organische affektive Störung', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F07.9', 'Nicht näher bezeichnete organische Persönlichkeits- und<br>Verhaltensstörung aufgrund einer Krankheit, Schädigung oder<br>Funktionsstörung des Gehirns', 'N4T', 'c', 'Organisches Psychosyndrom', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F09', 'Nicht näher bezeichnete organische oder symptomatische psychische<br>Störung', 'M3T', 'c', 'Psychose:<br>. organische o.n.A.<br>. symptomatische o.n.A.', 'Exkl.: Nicht näher bezeichnete Psychose (F29)', '', 'M', '3', '', '', '');
