INSERT INTO care_icd10_de VALUES ( 'F19.-', 'Psychische und Verhaltensstörungen durch multiplen<br>Substanzgebrauch und Konsum anderer psychotroper Substanzen', 'M3T', 'c', 'Inkl.:   Mißbrauch von Substanzen o.n.A.', '', '', 'M', '3', 'Diese Kategorie ist beim Konsum von zwei oder mehr psychotropen<br>Substanzen zu verwenden, wenn nicht entschieden werden kann,<br>welche Substanz die Störung ausgelöst hat. Diese Kategorie ist<br>außerdem zu verwenden, wenn nur eine oder keine der konsumierten<br>Substanzen nicht sicher zu identifizieren oder unbekannt sind, da<br>viele Konsumenten oft selbst nicht genau wissen, was sie<br>einnehmen.', '', '[Hinweise zu den Subkategorien siehe am Anfang dieser<br>Krankheitsgruppe]');
