INSERT INTO care_icd10_de VALUES ( 'F45.4', 'Anhaltende somatoforme Schmerzstörung', 'M4T', 'c', 'Psychalgie<br>Psychogen:<br>. Kopfschmerz<br>. Rückenschmerz<br>Somatoforme Schmerzstörung', 'Exkl.: Rückenschmerzen o.n.A. (M54.9-)<br>Schmerz:<br>. akut (R52.0)<br>. chronisch (R52.2)<br>. therapieresistent (R52.1)<br>. o.n.A. (R52.9)<br>Spannungskopfschmerz (G44.2)', '', 'M', '4', 'Die vorherrschende Beschwerde ist ein andauernder, schwerer und<br>quälender Schmerz, der durch einen physiologischen Prozeß oder<br>eine körperliche Störung nicht vollständig erklärt werden kann. Er<br>tritt in Verbindung mit emotionalen Konflikten oder psychosozialen<br>Belastungen auf, die schwerwiegend genug sein sollten, um als<br>entscheidende ursächliche Faktoren gelten zu können. Die Folge ist<br>meist eine beträchtlich gesteigerte persönliche oder medizinische<br>Hilfe und Unterstützung. Schmerzzustände mit vermutlich<br>psychogenem Ursprung, die im Verlauf depressiver Störungen oder<br>einer Schizophrenie auftreten, sollten hier nicht berücksichtigt<br>werden.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F45.8', 'Sonstige somatoforme Störungen', 'N4T', 'c', 'Psychogen:<br>. Dysmenorrhoe<br>. Dysphagie, einschließlich \"Globus hystericus\"<br>. Pruritus<br>. Tortikollis<br>. Zähneknirschen', '', '', 'N', '4', 'Hier sollten alle anderen Störungen der Wahrnehmung, der<br>Körperfunktion und des Krankheitsverhaltens klassifiziert werden,<br>die nicht durch das vegetative Nervensystem vermittelt werden, die<br>auf spezifische Teile oder Systeme des Körpers begrenzt sind und<br>mit belastenden Ereignissen oder Problemen eng in Verbindung<br>stehen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F45.9', 'Somatoforme Störung, nicht näher bezeichnet', 'N4T', 'c', 'Psychosomatische Störung o.n.A.', '', '', 'N', '4', '', '', '');
