INSERT INTO care_icd10_de VALUES ( 'F78.-', 'Andere Intelligenzminderung', 'M3T', 'c', '', '', '', 'M', '3', 'Diese Kategorie soll nur verwendet werden, wenn die Beurteilung<br>der Intelligenzminderung mit Hilfe der üblichen Verfahren wegen<br>begleitender sensorischer oder körperlicher Beeinträchtigungen<br>besonders schwierig oder unmöglich ist, wie bei Blinden,<br>Taubstummen, schwer verhaltensgestörten oder körperlich<br>behinderten Personen.', '', '');
