INSERT INTO care_icd10_de VALUES ( 'F79.-', 'Nicht näher bezeichnete Intelligenzminderung', 'M3T', 'c', 'Inkl.:   Geistig:<br>. Behinderung o.n.A.<br>. Defizite o.n.A.', '', '', 'M', '3', 'Die Informationen sind nicht ausreichend, die Intelligenzminderung<br>in eine der oben genannten Kategorien einzuordnen.', '', '');
