INSERT INTO care_icd10_de VALUES ( '(F80-F89)', 'Entwicklungsstörungen', '-1T', 'p', '', '', '', '-', '1', ' Die in diesem Abschnitt zusammengefaßten Störungen haben folgende<br>Gemeinsamkeiten:<br>P  a)  Beginn ausnahmslos im Kleinkindalter oder in der Kindheit
INSERT INTO care_icd10_de VALUES ( 'F80.-', 'Umschriebene Entwicklungsstörungen des Sprechens und der Sprache', 'M3T', 'c', '', '', '', 'M', '3', 'Es handelt sich um Störungen, bei denen die normalen Muster des<br>Spracherwerbs von frühen Entwicklungsstadien an beeinträchtigt<br>sind. Die Störungen können nicht direkt neurologischen Störungen<br>oder Veränderungen des Sprachablaufs, sensorischen<br>Beeinträchtigungen, Intelligenzminderung oder Umweltfaktoren<br>zugeordnet werden. Umschriebene Entwicklungsstörungen des<br>Sprechens und der Sprache ziehen oft sekundäre Folgen nach sich,<br>wie Schwierigkeiten beim Lesen und Rechtschreiben, Störungen im<br>Bereich der zwischenmenschlichen Beziehungen, im emotionalen und<br>Verhaltensbereich.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.0', 'Artikulationsstörung', 'N4T', 'c', 'Dyslalie<br>Entwicklungsbedingte Artikulationsstörung<br>Funktionelle Artikulationsstörung<br>Lallen<br>Phonologische Entwicklungsstörung', 'Exkl.: Artikulationsschwäche (bei):<br>. Aphasie o.n.A. (R47.0)<br>. Apraxie (R48.2)<br>. mit einer Entwicklungsstörung der Sprache:<br>  . expressiv (F80.1)<br>  . rezeptiv (F80.2)<br>. Hörverlust (H90-H91)<br>. Intelligenzminderung (F70-F79)', '', 'N', '4', 'Eine umschriebene Entwicklungsstörung, bei der die Artikulation<br>des Kindes unterhalb des seinem Intelligenzalter angemessenen<br>Niveaus liegt, seine sprachlichen Fähigkeiten jedoch im<br>Normbereich liegen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.1', 'Expressive Sprachstörung', 'N4T', 'c', 'Entwicklungsbedingte Dysphasie oder Aphasie, expressiver Typ', 'Exkl.: Dysphasie und Aphasie o.n.A. (R47.0)<br>Elektiver Mutismus (F94.0)<br>Entwicklungsbedingte Dysphasie oder Aphasie, rezeptiver<br>Typ (F80.2)<br>Erworbene Aphasie mit Epilepsie [Landau-Kleffner-Syndrom]<br>(F80.3)<br>Intelligenzminderung (F70-F79)<br>Tiefgreifende Entwicklungsstörungen (F84.-)', '', 'N', '4', 'Eine umschriebene Entwicklungsstörung, bei der die Fähigkeit des<br>Kindes, die expressiv gesprochene Sprache zu gebrauchen, deutlich<br>unterhalb des seinem Intelligenzalter angemessenen Niveaus liegt,<br>das Sprachverständnis liegt jedoch im Normbereich. Störungen der<br>Artikulation können vorkommen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.2', 'Rezeptive Sprachstörung', 'N4T', 'c', 'Angeborene fehlende akustische Wahrnehmung<br>Entwicklungsbedingt:<br>. Dysphasie oder Aphasie, rezeptiver Typ<br>. Wernicke-Aphasie<br>Worttaubheit', 'Exkl.: Autismus (F84.0-F84.1)<br>Dysphasie und Aphasie:<br>. expressiver Typ (F80.1)<br>. o.n.A. (R47.0)<br>Elektiver Mutismus (F94.0)<br>Erworbene Aphasie mit Epilepsie [Landau-Kleffner-Syndrom]<br>(F80.3)<br>Intelligenzminderung (F70-F79)<br>Sprachentwicklungsverzögerung infolge von Schwerhörigkeit<br>oder Taubheit (H90-H91)', '', 'N', '4', 'Eine umschriebene Entwicklungsstörung, bei der das<br>Sprachverständnis des Kindes unterhalb des seinem Intelligenzalter<br>angemessenen Niveaus liegt. In praktisch allen Fällen ist auch die<br>expressive Sprache deutlich beeinflußt, Störungen in der<br>Wort-Laut-Produktion sind häufig.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.3', 'Erworbene Aphasie mit Epilepsie [Landau-Kleffner-Syndrom]', 'N4T', 'c', '', 'Exkl.: Aphasie bei anderen desintegrativen Störungen des<br>Kindesalters (F84.2-F84.3)<br>Aphasie bei Autismus (F84.0-F84.1)<br>Aphasie o.n.A. (R47.0)', '', 'N', '4', 'Eine Störung, bei der ein Kind, welches vorher normale<br>Fortschritte in der Sprachentwicklung gemacht hatte, sowohl<br>rezeptive als auch expressive Sprachfertigkeiten verliert, die<br>allgemeine Intelligenz aber erhalten bleibt. Der Beginn der<br>Störung wird von paroxysmalen Auffälligkeiten im EEG begleitet und<br>in der Mehrzahl der Fälle auch von epileptischen Anfällen.<br>Typischerweise liegt der Beginn im Alter von 3-7 Jahren mit einem<br>Verlust der Sprachfertigkeiten innerhalb von Tagen oder Wochen.<br>Der zeitliche Zusammenhang zwischen dem Beginn der Krampfanfälle<br>und dem Verlust der Sprache ist variabel, wobei das eine oder das<br>andere um ein paar Monate bis zu zwei Jahren vorausgehen kann. Als<br>möglicher Grund für diese Störung ist ein entzündlicher<br>enzephalitischer Prozeß zu vermuten. Etwa zwei Drittel der<br>Patienten behalten einen mehr oder weniger rezeptiven<br>Sprachdefekt.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.8', 'Sonstige Entwicklungsstörungen des Sprechens oder der Sprache', 'N4T', 'c', 'Lispeln', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F80.9', 'Entwicklungsstörung des Sprechens oder der Sprache, nicht näher<br>bezeichnet', 'N4T', 'c', 'Sprachstörung o.n.A.', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.-', 'Umschriebene Entwicklungsstörungen schulischer Fertigkeiten', 'M3T', 'c', '', '', '', 'M', '3', 'Es handelt sich um Störungen, bei denen die normalen Muster des<br>Fertigkeitserwerbs von frühen Entwicklungstadien an gestört sind.<br>Dies ist nicht einfach Folge eines Mangels an Gelegenheit zu<br>lernen
INSERT INTO care_icd10_de VALUES ( 'F81.0', 'Lese- und Rechtschreibstörung', 'M4T', 'c', 'Entwicklungsdyslexie<br>Umschriebene Lesestörung<br>\"Leserückstand\"', 'Exkl.: Alexie o.n.A. (R48.0)<br>Dyslexie o.n.A. (R48.0)<br>Leseverzögerung infolge emotionaler Störung (F93.-)', '', 'M', '4', 'Das Hauptmerkmal ist eine umschriebene und bedeutsame<br>Beeinträchtigung in der Entwicklung der Lesefertigkeiten, die<br>nicht allein durch das Entwicklungsalter, Visusprobleme oder<br>unangemessene Beschulung erklärbar ist. Das Leseverständnis, die<br>Fähigkeit, gelesene Worte wiederzuerkennen, vorzulesen und<br>Leistungen, für welche Lesefähigkeit nötig ist, können sämtlich<br>betroffen sein. Bei umschriebenen Lesestörungen sind<br>Rechtschreibstörungen häufig und persistieren oft bis in die<br>Adoleszenz, auch wenn einige Fortschritte im Lesen gemacht werden.<br>Umschriebene Entwicklungsstörungen des Lesens gehen<br>Entwicklungsstörungen des Sprechens oder der Sprache voraus.<br>Während der Schulzeit sind begleitende Störungen im emotionalen<br>und Verhaltensbereich häufig.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.1', 'Isolierte Rechtschreibstörung', 'N4T', 'c', 'Umschriebene Verzögerung der Rechtschreibfähigkeit (ohne<br>Lesestörung)', 'Exkl.: Agraphie o.n.A. (R48.8)<br>Rechtschreibschwierigkeiten:<br>. durch inadäquaten Unterricht (Z65.-)<br>. mit Lesestörung (F81.0)', '', 'N', '4', 'Es handelt sich um eine Störung, deren Hauptmerkmal in einer<br>umschriebenen und bedeutsamen Beeinträchtigung der Entwicklung von<br>Rechtschreibfertigkeiten besteht, ohne Vorgeschichte einer<br>Lesestörung. Sie ist nicht allein durch ein zu niedriges<br>Intelligenzalter, durch Visusprobleme oder unangemessene<br>Beschulung erklärbar. Die Fähigkeiten, mündlich zu buchstabieren<br>und Wörter korrekt zu schreiben, sind beide betroffen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.2', 'Rechenstörung', 'N4T', 'c', 'Entwicklungsbedingtes Gerstmann-Syndrom<br>Entwicklungsstörung des Rechnens<br>Entwicklungs-Akalkulie', 'Exkl.: Akalkulie o.n.A. (R48.8)<br>Kombinierte Störung schulischer Fertigkeiten (F81.3)<br>Rechenschwierigkeiten, hauptsächlich durch inadäquaten<br>Unterricht (Z65.-)', '', 'N', '4', 'Diese Störung besteht in einer umschriebenen Beeinträchtigung von<br>Rechenfertigkeiten, die nicht allein durch eine allgemeine<br>Intelligenzminderung oder eine unangemessene Beschulung erklärbar<br>ist. Das Defizit betrifft vor allem die Beherrschung grundlegender<br>Rechenfertigkeiten, wie Addition, Subtraktion, Multiplikation und<br>Division, weniger die höheren mathematischen Fertigkeiten, die für<br>Algebra, Trigonometrie, Geometrie oder Differential- und<br>Integralrechnung benötigt werden.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.3', 'Kombinierte Störungen schulischer Fertigkeiten', 'M4T', 'c', '', 'Exkl.: Isolierte Rechtschreibstörung (F81.1)<br>Lese- und Rechtschreibstörung (F81.0)<br>Rechenstörung (F81.2)', '', 'M', '4', 'Dies ist eine schlecht definierte Restkategorie für Störungen mit<br>deutlicher Beeinträchtigung der Rechen-, der Lese- und der<br>Rechtschreibfähigkeiten. Die Störung ist jedoch nicht allein durch<br>eine allgemeine Intelligenzminderung oder eine unangemessene<br>Beschulung erklärbar. Sie soll für Störungen verwendet werden, die<br>die Kriterien für F81.2 und F81.0 oder F81.1 erfüllen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.8', 'Sonstige Entwicklungsstörungen schulischer Fertigkeiten', 'N4T', 'c', 'Entwicklungsbedingte expressive Schreibstörung', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F81.9', 'Entwicklungsstörung schulischer Fertigkeiten, nicht näher<br>bezeichnet', 'N4T', 'c', 'Lernbehinderung o.n.A.<br>Lernstörung o.n.A.<br>Störung des Wissenserwerbs o.n.A.', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F82', 'Umschriebene Entwicklungsstörung der motorischen Funktionen', 'M3T', 'c', 'Entwicklungsbedingte Koordinationsstörung<br>Entwicklungsdyspraxie<br>Syndrom des ungeschickten Kindes', 'Exkl.: Koordinationsstörungen infolge einer Intelligenzminderung<br>(F70-F79)<br>Koordinationsverlust (R27.-)<br>Störungen des Ganges und der Mobilität (R26.-)', '', 'M', '3', 'Hauptmerkmal ist eine schwerwiegende Entwicklungsbeeinträchtigung<br>der motorischen Koordination, die nicht allein durch eine<br>Intelligenzminderung oder eine spezifische angeborene oder<br>erworbene neurologische Störung erklärbar ist. In den meisten<br>Fällen zeigt eine sorgfältige klinische Untersuchung dennoch<br>deutliche entwicklungsneurologische Unreifezeichen wie choreoforme<br>Bewegungen freigehaltener Glieder oder Spiegelbewegungen und<br>andere begleitende motorische Merkmale, ebenso wie Zeichen einer<br>mangelhaften fein- oder grobmotorischen Koordination.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F83', 'Kombinierte umschriebene Entwicklungsstörungen', 'M3T', 'c', '', '', '', 'M', '3', 'Dies ist eine Restkategorie für Störungen, bei denen eine gewisse<br>Mischung von umschriebenen Entwicklungsstörungen des Sprechens und<br>der Sprache, schulischer Fertigkeiten und motorischer Funktionen<br>vorliegt, von denen jedoch keine so dominiert, daß sie eine<br>Hauptdiagnose rechtfertigt. Diese Mischkategorie soll nur dann<br>verwendet werden, wenn weitgehende überschneidungen mit allen<br>diesen umschriebenen Entwicklungsstörungen vorliegen. Meist sind<br>die Störungen mit einem gewissen Grad an allgemeiner<br>Beeinträchtigung kognitiver Funktionen verbunden. Sie ist also<br>dann zu verwenden, wenn Funktionsstörungen vorliegen, welche die<br>Kriterien von zwei oder mehr Kategorien von F80.-, F81.- und F82<br>erfüllen.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F84.-', 'Tiefgreifende Entwicklungsstörungen', 'M3T', 'c', '', '', '', 'M', '3', 'Diese Gruppe von Störungen ist gekennzeichnet durch qualitative<br>Abweichungen in den wechselseitigen sozialen Interaktionen und<br>Kommunikationsmustern und durch ein eingeschränktes, stereotypes,<br>sich wiederholendes Repertoire von Interessen und Aktivitäten.<br>Diese qualitativen Auffälligkeiten sind in allen Situationen ein<br>grundlegendes Funktionsmerkmal des betroffenen Kindes.', 'Sollen alle begleitenden somatischen Zustandsbilder und<br>Intelligenzminderung angegeben werden, sind zusätzliche<br>Schlüsselnummern zu benutzen. Im Krankenhaus sollte diese<br>Information immer verschlüsselt werden, wenn sie vorliegt.', '');
INSERT INTO care_icd10_de VALUES ( 'F84.0', 'Frühkindlicher Autismus', 'N4T', 'c', 'Autistische Störung<br>Frühkindliche Psychose<br>Infantiler Autismus<br>Kanner-Syndrom', 'Exkl.: Autistische Psychopathie (F84.5)', '', 'N', '4', 'Diese Form der tiefgreifenden Entwicklungsstörung ist durch eine<br>abnorme oder beeinträchtigte Entwicklung definiert, die sich vor<br>dem dritten Lebensjahr manifestiert. Sie ist außerdem<br>gekennzeichnet durch ein charakteristisches Muster abnormer<br>Funktionen in den folgenden psychopathologischen Bereichen: in der<br>sozialen Interaktion, der Kommunikation und im eingeschränkten<br>stereotyp repetitiven Verhalten. Neben diesen spezifischen<br>diagnostischen Merkmalen zeigt sich häufig eine Vielzahl<br>unspezifischer Probleme, wie Phobien, Schlaf- und Eßstörungen,<br>Wutausbrüche und (autodestruktive) Aggression.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F84.1', 'Atypischer Autismus', 'N4T', 'c', 'Atypische kindliche Psychose<br>Intelligenzminderung mit autistischen Zügen', '', '', 'N', '4', 'Diese Form der tiefgreifenden Entwicklungsstörung unterscheidet<br>sich vom frühkindlichen Autismus entweder durch das Alter bei<br>Krankheitsbeginn oder dadurch, daß die diagnostischen Kriterien<br>nicht in allen genannten Bereichen erfüllt werden. Diese<br>Subkategorie sollte immer dann verwendet werden, wenn die abnorme<br>oder beeinträchtigte Entwicklung erst nach dem dritten Lebensjahr<br>manifest wird und wenn nicht in allen für die Diagnose Autismus<br>geforderten psychopathologischen Bereichen (nämlich wechselseitige<br>soziale Interaktionen, Kommunikation und eingeschränktes,<br>stereotyp repetitives Verhalten) Auffälligkeiten nachweisbar sind,<br>auch wenn charakteristische Abweichungen auf anderen Gebieten<br>vorliegen. Atypischer Autismus tritt sehr häufig bei schwer<br>retardierten bzw. unter einer schweren rezeptiven Störung der<br>Sprachentwicklung leidenden Patienten auf.', 'Soll eine Intelligenzminderung angegeben werden, ist eine<br>zusätzliche Schlüsselnummer (F70-F79) zu benutzen. Im Krankenhaus<br>sollte diese Information immer verschlüsselt werden, wenn sie<br>vorliegt.', '');
INSERT INTO care_icd10_de VALUES ( 'F84.2', 'Rett-Syndrom', 'N4T', 'c', '', '', '', 'N', '4', 'Dieses Zustandsbild wurde bisher nur bei Mädchen beschrieben
INSERT INTO care_icd10_de VALUES ( 'F84.3', 'Andere desintegrative Störung des Kindesalters', 'N4T', 'c', 'Dementia infantilis<br>Desintegrative Psychose<br>Heller-Syndrom<br>Symbiotische Psychose', 'Exkl.: Rett-Syndrom (F84.2)', '', 'N', '4', 'Diese Form einer tiefgreifenden Entwicklungsstörung ist - anders<br>als das Rett-Syndrom - durch eine Periode einer zweifellos<br>normalen Entwicklung vor dem Beginn der Krankheit definiert. Es<br>folgt ein Verlust vorher erworbener Fertigkeiten verschiedener<br>Entwicklungsbereiche innerhalb weniger Monate. Typischerweise wird<br>die Störung von einem allgemeinen Interessenverlust an der Umwelt,<br>von stereotypen, sich wiederholenden motorischen Manierismen und<br>einer autismusähnlichen Störung sozialer Interaktionen und der<br>Kommunikation begleitet. In einigen Fällen kann die Störung einer<br>begleitenden Enzephalopathie zugeschrieben werden, die Diagnose<br>ist jedoch anhand der Verhaltensmerkmale zu stellen.', 'Soll eine begleitende neurologische Krankheit angegeben werden,<br>ist eine zusätzliche Schlüsselnummer zu benutzen. Im Krankenhaus<br>sollte diese Information immer verschlüsselt werden, wenn sie<br>vorliegt.', '');
INSERT INTO care_icd10_de VALUES ( 'F84.4', 'überaktive Störung mit Intelligenzminderung und<br>Bewegungsstereotypien', 'N4T', 'c', '', '', '', 'N', '4', 'Dies ist eine schlecht definierte Störung von unsicherer<br>nosologischer Validität. Diese Kategorie wurde für eine Gruppe von<br>Kindern mit schwerer Intelligenzminderung (IQ unter 34)<br>eingeführt, mit erheblicher Hyperaktivität,<br>Aufmerksamkeitsstörungen und stereotypen Verhaltensweisen. Sie<br>haben meist keinen Nutzen von Stimulanzien (anders als Kinder mit<br>einem IQ im Normbereich) und können auf eine Verabreichung von<br>Stimulanzien eine schwere dysphorische Reaktion - manchmal mit<br>psychomotorischer Entwicklungsverzögerung - zeigen. In der<br>Adoleszenz kann sich die Hyperaktivität in eine verminderte<br>Aktivität wandeln, ein Muster, das bei hyperkinetischen Kindern<br>mit normaler Intelligenz nicht üblich ist. Das Syndrom wird häufig<br>von einer Vielzahl von umschriebenen oder globalen<br>Entwicklungsverzögerungen begleitet. Es ist nicht bekannt, in<br>welchem Umfang das Verhaltensmuster dem niedrigen IQ oder einer<br>organischen Hirnschädigung zuzuschreiben ist.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F84.5', 'Asperger-Syndrom', 'N4T', 'c', 'Autistische Psychopathie<br>Schizoide Störung des Kindesalters', '', '', 'N', '4', 'Diese Störung von unsicherer nosologischer Validität ist durch<br>dieselbe Form qualitativer Abweichungen der wechselseitigen<br>sozialen Interaktionen, wie für den Autismus typisch,<br>charakterisiert, zusammen mit einem eingeschränkten, stereotypen,<br>sich wiederholenden Repertoire von Interessen und Aktivitäten. Die<br>Störung unterscheidet sich vom Autismus in erster Linie durch<br>fehlende allgemeine Entwicklungsverzögerung bzw. den fehlenden<br>Entwicklungsrückstand der Sprache und der kognitiven Entwicklung.<br>Die Störung geht häufig mit einer auffallenden Ungeschicklichkeit<br>einher. Die Abweichungen tendieren stark dazu, bis in die<br>Adoleszenz und das Erwachsenenalter zu persistieren. Gelegentlich<br>treten psychotische Episoden im frühen Erwachsenenleben auf.', '', '');
INSERT INTO care_icd10_de VALUES ( 'F84.8', 'Sonstige tiefgreifende Entwicklungsstörungen', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'F84.9', 'Tiefgreifende Entwicklungsstörung, nicht näher bezeichnet', 'N4T', 'c', '', '', '', 'N', '4', '', '', '');
