INSERT INTO care_icd10_de VALUES ( 'G09', 'Folgen entzündlicher Krankheiten des Zentralnervensystems', 'M3T', 'c', '', '', 'Hinweis: Soll bei einer anderenorts klassifizierten Störung<br>angegeben werden, daß sie Folge eines primär unter<br>G00-G08 (mit Ausnahme der Stern-Kategorien)<br>klassifizierbaren Zustandes ist, so ist (statt einer<br>Schlüsselnummer aus G00-G08) die vorliegende Kategorie zu<br>verwenden. Zu den \"Folgen\" zählen Krankheitszustände, die<br>als Folgen oder Spätfolgen bezeichnet sind oder die ein<br>Jahr oder länger seit Beginn des verursachenden Leidens<br>bestehen. Für den Gebrauch dieser Kategorie sollten die<br>betreffenden Regeln und Richtlinien zur Verschlüsselung<br>der Morbidität und Mortalität in Band 2 (Regelwerk)<br>herangezogen werden.', 'M', '3', '', '', '');
