INSERT INTO care_icd10_de VALUES ( '(V01-Y98)', 'Äußere Ursachen von Morbidität und Mortalität', '-0T', 'p', '', '', 'Dieses Kapitel, das in den vorangegangenen Revisionen der ICD als<br>ergänzende Klassifikation bestand, ermöglicht die Klassifizierung von<br>Umweltereignissen und Umständen als Ursache von Verletzungen, Vergiftungen<br>und anderen schädlichen Wirkungen. In Fällen, in denen eine Schlüsselnummer<br>aus diesem Kapitel anwendbar ist, soll diese zusätzlich zu einer die Art<br>des Zustandes bezeichnenden Schlüsselnummer aus einem anderen Kapitel der<br>Klassifikation benutzt werden. Meistens wird der Zustand mit einer<br>Schlüsselnummer aus dem Kapitel XIX \"Verletzungen, Vergiftungen und<br>bestimmte andere Folgen äußerer Ursachen (S00-T98)\" zu klassifizieren sein.', '-', '0', '', '', '');
