INSERT INTO care_icd10_de VALUES ( 'V99!', ' Transportmittelunfall', 'Z3T', 'c', 'Inkl.:   Busunfall<br>Eisenbahnunfall<br>Fahrradunfall<br>Fußgängerunfall<br>Lieferwagenunfall<br>LKW-Unfall<br>Luftverkehrsunfall<br>Motorradunfall<br>PKW-Unfall<br>Reitunfall oder Unfall eines tierbespannten Fahrzeuges<br>Straßenbahnunfall<br>Unfall mit einem Spezialfahrzeug<br>Wasserfahrzeugunfall', 'Exkl.: Ereignis, dessen Umstände unbestimmt sind (Y34.9)<br>Ertrinken und Untergehen durch freiwilligen Sprung von<br>einem Boot, das nicht an einem Unfall beteiligt ist<br>(X59.9)<br>Exposition gegenüber Luftdruckwechsel beim Aufsteigen<br>oder Landen (W94.9)<br>Spezialfahrzeug bei Benutzung im Stand oder bei<br>Instandhaltung (W49.9)<br>Tätlicher Angriff durch vorsätzlich verursachten<br>Kraftfahrzeugunfall (Y09.9)<br>Vorsätzliche Selbstbeschädigung (X84.9)<br>Zusammenstoß eines Fußgängers (oder eines von ihm<br>benutzten Beförderungsmittels) mit anderem Fußgänger<br>(oder von diesem benutzten Beförderungsmittel)<br>(X59.9)', '', 'Z', '3', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'W49.9!', 'Unfall durch Exposition gegenüber mechanischen Kräften<br> unbelebter Objekte', 'Z4T', 'c', 'Inkl.:   Exposition gegenüber:<br>. Lärm<br>. Vibration<br>Unfall durch:<br>. Eindringen eines Fremdkörpers durch die Haut<br>. (fallende) (geworfene) Gegenstände<br>. Feuerwaffen<br>. Feuerwerkskörper<br>. Kesselexplosion<br>. Maschinen<br>. Messerstich<br>. Werkzeuge', 'Exkl.: Ätzende Flüssigkeit (X49.9)<br>Aspiration oder Verschlucken eines Fremdkörpers mit<br>Verschluß der Atemwege (X59.9)<br>Einsturz eines brennenden Gebäudes (X59.9)<br>Exposition gegenüber elektrischem Strom (W87.9)<br>Fallender Gegenstand bei Naturkatastrophe (X59.9)<br>Kontakt oder Zusammenstoß mit Tieren oder Personen<br>(W64.9)<br>Sturz im Zusammenhang mit Glas (X59.9)<br>Tätlicher Angriff (Y09.9)<br>Vorsätzliche Selbstbeschädigung (X84.9)', '', 'Z', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'W64.9!', 'Unfall durch Exposition gegenüber mechanischen Kräften<br> belebter Objekte', 'Z4T', 'c', 'Inkl.:   Gequetscht- oder Gestoßenwerden bei Menschenansammlung<br>oder von in Panik geratener Menschenmenge<br>Insektenstich, nichtgiftig<br>Tierbiß (nichtgiftig)<br>Verletzung an Pflanzen (nichtgiftig)', 'Exkl.: Bisse, giftig (X29.9)<br>Getroffenwerden von Gegenständen (W49.9)<br>Stiche (giftig) (X29.9)<br>Sturz durch Zusammenstoß eines Fußgängers (oder eines von<br>ihm benutzten Beförderungsmittels) mit anderem<br>Fußgänger (oder von diesem benutzten<br>Beförderungsmittel) (X59.9)<br>Tätlicher Angriff (Y09.9)', '', 'Z', '4', '', '', '');
