INSERT INTO care_icd10_de VALUES ( 'X19.9!', 'Verbrennung oder Verbrühung durch Hitze oder heiße Substanzen', 'Z4T', 'c', 'Verbrennung oder Verbrühung durch heiße:<br>. Dämpfe<br>. Flüssigkeiten<br>. Gase<br>. Gegenstände<br>. Nahrungsmittel<br>. sonstige Materialien', 'Exkl.: Exposition gegenüber:<br>. Feuer und Flammen (X59.9)<br>. übermäßige natürliche Hitze (X59.9)<br>Gegenstände, die üblicherweise nicht heiß sind, z.B. ein<br>Gegenstand, der durch einen Hausbrand erhitzt wurde<br>(X59.9)', '', 'Z', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'X29.9!', 'Unfall durch Kontakt mit giftigen Pflanzen oder Tieren', 'Z4T', 'c', 'Inkl.:   Giftiger Tierbiß oder Insektenstich<br>Verletzung an giftigen Pflanzen', 'Exkl.: Verzehr von giftigen Tieren oder Pflanzen (X49.9)<br>Echse (nichtgiftig) (W64.9)<br>Schlange, nichtgiftig (W64.9)<br>Meerestiere, nichtgiftig (W64.9)<br>Stichwunde o.n.A. durch Pflanzendornen oder -stacheln<br>(W64.9)', '', 'Z', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'X49.9!', 'Akzidentelle Vergiftung', 'Z4T', 'c', 'Inkl.:   Akzidentelle überdosierung eines Arzneimittels oder einer<br>Droge<br>Irrtümliche Verabreichung oder Einnahme eines falschen<br>Arzneimittels<br>Vergiftung (akzidentell) durch und Exposition gegenüber:<br>. Alkohol<br>. Arzneimittel, Drogen und sonstige biologisch aktive<br>Substanzen<br>. ätzende Flüssigkeit<br>. halogenierte Kohlenwasserstoffe<br>. organische Lösungsmittel<br>. Schädlingsbekämpfungsmittel<br>Verzehr von giftigen Tieren und Pflanzen', 'Exkl.: Anwendung in suizidaler Absicht oder zum Zwecke der<br>Tötung oder Schädigung oder bei sonstigen, unter X84.9,<br>Y09.9, Y34.9 klassifizierbaren Sachverhalten<br>Kontakt mit giftigen Tieren und Pflanzen (X29.9)<br>Unerwünschte Nebenwirkung durch indikationsgerecht<br>angewendete und in therapeutischer oder<br>prophylaktischer Dosierung korrekt verabreichte<br>Arzneimittel (Y57.9, Y59.9)', '', 'Z', '4', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'X59.9!', 'Sonstiger und nicht näher bezeichneter Unfall', 'Z4T', 'c', 'Inkl.:   (Akzidentelle) Exposition gegenüber sonstigen Faktoren<br>Aspiration<br>Einsturz eines brennenden Gebäudes<br>Ersticken im Bett<br>Ertrinken und Untergehen<br>Exposition gegenüber:<br>. Feuer und Flammen<br>. Rauch<br>. Sonnenlicht<br>. übermäßige natürliche Hitze und Kälte<br>Fallende Gegenstände bei Naturkatastrophen<br>Opfer von :<br>. Blitzschlag<br>. Erdbeben<br>. Lawine<br>. Sturmkatastrophe<br>. überschwemmung<br>Strangulierung<br>Unfall durch Sturz (ins Wasser)<br>Verletzung oder Krankheit durch überanstrengung sowie<br>durch anstrengende oder wiederholte Bewegungen, auch<br>beim Sport', 'Exkl.: Berührung mit oder Inhalation von:<br>. Flüssiggas (W93.9)<br>. Trockeneis (W93.9)<br>Brandstiftung (Y09.9)<br>Durch Explosion entstandener Brand (W49.9)<br>Ertrinken und Untergehen durch:<br>. Transportmittelunfälle (V99)<br>. Wasserfahrzeugunfall (V99)<br>Gequetscht- oder Gestoßenwerden bei Menschenansammlung<br>oder von in Panik geratener Menschenmenge (W64.9)<br>Künstlich erzeugte Kälte (W93.9)<br>Obstruktion des Ösophagus durch Nahrungsmittel,<br>Fremdkörper oder Erbrochenes, ohne Angabe von Asphyxie<br>oder Obstruktion der Atemwege (W49.9)<br>Sturz (aus) (in) (von):<br>. im Zusammenhang mit Geräten und Anlagen für<br>Freizeitgestaltung (W49.9)<br>. Maschinen (in Betrieb) (W49.9)<br>. Transportfahrzeug (V99)<br>Tätlicher Angriff (Y09.9)<br>Transportmittelunfälle (V99)<br>übermäßige, künstlich erzeugte Hitze (W92.9)<br>Verletzung, ausgenommen Asphyxie oder Obstruktion der<br>Atemwege, durch Nahrungsmittel, Fremdkörper oder<br>Erbrochenes (W49.9)<br>Verletzung durch Umstürzen von Bäumen oder sonstigen<br>Objekten durch Blitzschlag (W49.9)<br>Vernachlässigung oder Verlassen (Y09.9)<br>Verschüttetwerden ohne Asphyxie oder Ersticken (W49.9)', '', 'Z', '4', '', '', '');
