INSERT INTO care_icd10_de VALUES ( '(Y40-Y84)', 'Komplikationen bei der medizinischen und chirurgischen Behandlung', '-1T', 'p', 'Inkl.: Chirurgische und medizinische Maßnahmen als Ursache einer abnormen <br>Reaktion eines Patienten oder einer späteren Komplikation, ohne<br>Angabe eines Zwischenfalls zum Zeitpunkt der Durchführung der<br>Maßnahme<br>Indikationsgerecht angewendetes und in therapeutischer oder <br>prophylaktischer Dosierung korrekt verabreichtes Arzneimittel<br>als Ursache einer unerwünschten Nebenwirkung <br> Komplikationen durch medizintechnische Geräte und Produkte <br> Zwischenfälle bei der medizinischen und chirurgischen Behandlung', 'Exkl.: Akzidentelle überdosierung eines Arzneimittels oder einer Droge, <br>irrtümliche Verabreichung oder Einnahme eines falschen<br>Arzneimittels (X49.9)', '', '-', '1', '', '', '');
INSERT INTO care_icd10_de VALUES ( 'Y57.9!', 'Komplikationen durch Arzneimittel oder Drogen', 'Z4T', 'c', 'Inkl.:   Unerwünschte Nebenwirkung von Arzneimitteln und Drogen<br>bei indikationsgerechter Anwendung und in korrekter<br>therapeutischer oder prophylaktischer Dosierung', 'Exkl.: Unfälle bei der Verabreichungsmethode von Arzneimitteln,<br>Drogen oder biologisch aktiven Substanzen bei<br>medizinischen und chirurgischen Maßnahmen (Y69)', '', 'Z', '4', '', '', '');
