INSERT INTO care_icd10_de VALUES ( 'Y69!', ' Zwischenfälle bei chirurgischem Eingriff und medizinischer<br>Behandlung', 'Z3T', 'c', 'Inkl.:   Dosierungsfehler<br>Kontaminierte Substanzen<br>Unzulängliche aseptische Kautelen<br>Versehentlich im Körper zurückgelassener Fremdkörper<br>Versehentliche(r) Schnitt, Punktion, Perforation oder<br>Blutung<br>Vorzeitiger Behandlungsabbruch', 'Exkl.: Chirurgische und medizinische Maßnahmen als Ursache einer<br>abnormen Reaktion eines Patienten oder einer späteren<br>Komplikation, ohne Angabe eines Zwischenfalls zum<br>Zeitpunkt der Durchführung der Maßnahme (Y84.9)<br>Medizintechnische Geräte und Produkte im Zusammenhang mit<br>Zwischenfällen bei diagnostischer und therapeutischer<br>Anwendung (Y82.8)', '', 'Z', '3', '', '', '');
