INSERT INTO care_ops301_de VALUES ( '1-210', 'Nichtinvasive präoperative Video-EEG-Intensivdiagnostik bei Epilepsie', '', '', 'Hinweis:  Dieser Kode umfaßt:<br>das Anbringen von dichtgesetzten Oberflächenelektroden und das ggf. durchgeführte Einbringen von Sphenoidalelektroden,<br>das Video-EEG-Intensivmonitoring für i.d.R. mindestens 3 Tage,<br>die Begleitung, Dokumentation und Auswertung (Medizin, MTA, Medizintechnik, Medizinphysik),<br>die psychosoziale Betreuung des Patienten während des diagnostischen Prozesses<br>Für die Durchführung gelten die Qualitätsstandards der Arbeitsgemeinschaft für präoperative Epilepsiediagnostik und operative Epilepsietherapie', 'B', '3', '');
